import React, { useEffect, useState } from "react";
import TableItems from "./TableItems";
import axios from "axios";

function CurrencyTable() {
  const [resultData, setResultData] = useState({
    rub: [],
    usd: [],
    eur: [],
  });
  const [rateArr, setRateArr] = useState(({
    // firstRub: [],
    // secondRub: [],
    // thirdRub: [],
    //
    // firstEur: [],
    // secondEur: [],
    // thirdEur: [],
    //
    // firstUsd: [],
    // secondUsd: [],
    // thirdUsd: []

    RUB_USDf: [],
    RUB_USDs: [],
    RUB_USDt: []

  }))
  const [titles] = React.useState([
    {id: 1, title: 'Pair name/market'},
    {id: 2, title: 'First'},
    {id: 3, title: 'Second'},
    {id: 4, title: 'Third'},
  ]);

  const updateDataHelper = (first, second, third) => {
    const rubArray = [first.RUB, second.RUB, third.RUB];
    const eurArray = [first.EUR, second.EUR, third.EUR];
    const usdArray = [first.USD, second.USD, third.USD];

    setResultData((prevState) => ({
      ...prevState,
      rub: rubArray,
      eur: eurArray,
      usd: usdArray,
    }));
    setRateArr((prevState) => ({
      ...prevState,
      // firstRub: first.RUB/first.EUR,
      // secondRub: second.RUB,
      // thirdRub: third.RUB,
      //
      // firstEur: first.EUR,
      // secondEur: second.EUR,
      // thirdEur: third.EUR,
      //
      // firstUsd: first.USD,
      // secondUsd: second.USD,
      // thirdUsd: third.USD,

      RUB_USDf: first.RUB/first.EUR,
      RUB_USDs: second.RUB/second.EUR,
      RUB_USDt: third.RUB/third.EUR

      // RUB_EURf:
      // RUB_EURs:
      // RUB_EURf:

      // EUR_USDf:
      // EUR_USDs:
      // EUR_USDt:
    }))
  };

  console.log(resultData)
  useEffect(() => {
    subscribe();
  }, []);

  const subscribe = async () => {
    try {
      const responseF = await axios.get('http://localhost:3010/api/v1/first/poll');
      const responseS = await axios.get('http://localhost:3010/api/v1/second/poll');
      const responseT = await axios.get('http://localhost:3010/api/v1/third/poll');


      updateDataHelper(
        responseF.data.rates,
        responseS.data.rates,
        responseT.data.rates
      );

      await subscribe();
    } catch (e) {
      setTimeout(() => {
        subscribe();
      }, 500);
    }
  };
  console.log(rateArr)

  return (
    <TableItems
    resultData={resultData}
    rateArr={rateArr}
    titles={titles}
  />
);
  // (
  //   <div>
  //     RUB
  //     {resultData.rub.map((value) => (
  //       <div key={value}>{value}</div>
  //     ))}
  //     USD
  //     {resultData.usd.map((value) => (
  //       <div key={value}>{value}</div>
  //     ))}
  //     EUR
  //     {resultData.eur.map((value) => (
  //       <div key={value}>{value}</div>
  //     ))}
  //   </div>
  // );
}

export default CurrencyTable;
